DROP TABLE IF EXISTS `system_field_option`;
CREATE TABLE `system_field_option`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `class_path` varchar(128) NOT NULL COMMENT '选项类型',
  `category` varchar(64) NOT NULL DEFAULT '' COMMENT '分类',
  `option_value` varchar(64) NOT NULL COMMENT '选项值',
  `option_display` varchar(128) NOT NULL COMMENT '选项显示',
  `option_group` varchar(64) NULL DEFAULT NULL COMMENT '选项分组',
  `option_icon` varchar(50) NULL DEFAULT NULL,
  `option_style` varchar(128) NULL DEFAULT NULL,
  `disabled` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否禁用',
  `properties` text NULL COMMENT '选项的属性,json存储',
  `state_form_revision` bigint(20) NOT NULL DEFAULT 1,
  `state_form_status` varchar(32) NOT NULL DEFAULT 'enabled' COMMENT '表单状态',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_system_field_option`(`class_path`, `category`, `option_value`) USING BTREE,
  INDEX `idx_system_field_option_category`(`class_path`, `category`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for system_form_actions
-- ----------------------------
DROP TABLE IF EXISTS `system_form_actions`;
CREATE TABLE `system_form_actions`  (
  `form_name` varchar(128) NOT NULL COMMENT '表单名称',
  `action_name` varchar(128) NOT NULL COMMENT '事件名称',
  `form_backend` varchar(64) NOT NULL COMMENT '后端服务实例名',
  `action_key` varchar(255) NOT NULL COMMENT '事件唯一键（<form_name>::<action_name>）',
  `action_display` varchar(255) NOT NULL COMMENT '显示名',
  `action_type` varchar(32) NOT NULL DEFAULT '' COMMENT '事件类型',
  `action_form_type` varchar(16) NOT NULL DEFAULT '' COMMENT '操作界面类型：NULL - 无界面; EDIT - 编辑; VIEW - 视图;',
  `source_state` varchar(255) NULL DEFAULT NULL COMMENT '操作前的初始状态值',
  `target_state` varchar(255) NULL DEFAULT NULL COMMENT '操作后的目标状态值',
  `prepare_required` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否支持操作执行前的数据准备接口',
  `message_reuqired` tinyint(1) NULL DEFAULT 0 COMMENT '是否要求填写操作说明:NULL - 不需要; TRUE - 必填; FALSE - 选填',
  `comfirm_reuqired` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否在执行操作前做二次确认',
  `revision_ignored` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否跳过版本自动升级',
  `action_async` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否异步执行',
  `authority_type` varchar(32) NOT NULL DEFAULT '' COMMENT '授权的范围',
  `authority_parser` varchar(180) NOT NULL DEFAULT '' COMMENT '授权对象解析器',
  `authority_cheker` varchar(180) NOT NULL DEFAULT '' COMMENT '特殊授权验证器',
  `last_updated` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '最后更新时间',
  PRIMARY KEY (`form_name`, `action_name`) USING BTREE,
  INDEX `idx_form_name`(`form_name`) USING BTREE,
  INDEX `idx_form_action_key`(`action_key`(191)) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

