DROP TABLE IF EXISTS `system_internal_issue`;
CREATE TABLE `system_internal_issue`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(128) NOT NULL,
  `category` varchar(64) NOT NULL DEFAULT 'issue' COMMENT '问题分类: 问题(issue)/优化(enhance)/需求(feature)/其它(other)',
  `description` varchar(5000) NULL DEFAULT NULL,
  `assignee` bigint(128) NULL DEFAULT NULL COMMENT '当前受理人',
  `assign_date` datetime(0) NULL DEFAULT NULL COMMENT '要求的完成时间',
  `result` varchar(64) NULL DEFAULT NULL COMMENT '处理结果：解决(resolved)/无效问题(invalid)/无法处理(powerless)',
  `resolution` varchar(5000) NULL DEFAULT NULL COMMENT '解决方案：详细描述问题分析及解决方式',
  `submitter` bigint(128) NOT NULL COMMENT '表单的创建人，该字段有状态机管理，不可随意变更',
  `created_at` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '表单的创建时间，该字段有状态机管理，不可随意变更',
  `state_form_status` varchar(64) NOT NULL DEFAULT 'SUBMITTED' COMMENT '表单的状态，该字段有状态机管理，不可随意变更',
  `state_form_revision` bigint(20) UNSIGNED NOT NULL DEFAULT 0 COMMENT '表单的版本，该字段有状态机管理，不可随意变更',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

